import 'bloc/card_added_bloc.dart';import 'models/card_added_model.dart';import 'package:flutter/material.dart';import 'package:rhs_app/core/app_export.dart';import 'package:rhs_app/widgets/app_bar/appbar_leading_image.dart';import 'package:rhs_app/widgets/app_bar/appbar_title.dart';import 'package:rhs_app/widgets/app_bar/appbar_trailing_image.dart';import 'package:rhs_app/widgets/app_bar/custom_app_bar.dart';import 'package:rhs_app/widgets/custom_elevated_button.dart';import 'package:rhs_app/widgets/custom_radio_button.dart';class CardAddedScreen extends StatelessWidget {const CardAddedScreen({Key? key}) : super(key: key);

static Widget builder(BuildContext context) { return BlocProvider<CardAddedBloc>(create: (context) => CardAddedBloc(CardAddedState(cardAddedModelObj: CardAddedModel()))..add(CardAddedInitialEvent()), child: CardAddedScreen()); } 
@override Widget build(BuildContext context) { mediaQueryData = MediaQuery.of(context); return SafeArea(child: Scaffold(appBar: _buildAppBar(context), body: Container(width: double.maxFinite, padding: EdgeInsets.symmetric(horizontal: 24.h, vertical: 28.v), child: Column(children: [_buildPaymentMethods(context), SizedBox(height: 34.v), _buildPayWithDebitCredit(context), SizedBox(height: 5.v)])), bottomNavigationBar: _buildContinue(context))); } 
